mapper.select:
select device_id as id, device.name, time, cars_number value
from device,
crowd_situation
where crowd_situation.time in
(select max(time)
from crowd_situation
where `time` between #{start} and #{end}
and device_id=#{id}
group by device_id
)
and device.id = crowd_situation.device_id;

select cars_number value
from crowd_situation
where time in
      (select max(time)
       from crowd_situation
       where `time` between #{start} and #{end}
         and device_id = #{id}
      )

select DATE_FORMAT(time, "%Y-%m-%d") time, max(cars_number) value, device.name
from crowd_situation,
     device
where time between #{start} and #{end}
  and device.id = crowd_situation.device_id
group by day(time), device_id
order by time;
